mapper.select:
select *
from consume
where id = #{id:VARCHAR} and code= #{code:INTEGER}

select num,id, f.code,f.name as fname, eatdate,count
from consume c
join food f
using(code)
where id = #{id}

mapper.insert:
insert into consume (id, code, eatdate, count)
values (#{id:VARCHAR}
,#{code:INTEGER}
,now()
,#{count:INTEGER})

mapper.update:
update consume set id = #{id:VARCHAR}
        		 ,code = #{code:INTEGER}
       			 ,eatdate= now()
      			 ,count = #{count:INTEGER}
where  num = #{num:INTEGER}
