mapper.insert:

		insert into user (id, password, name, address, phone, allergy)
		values ( #{id:VARCHAR}
		        ,#{password:VARCHAR}
		        ,#{name:VARCHAR}
		        ,#{address:VARCHAR}
		        ,#{phone:VARCHAR}
		        ,#{allergy:VARCHAR})

mapper.update:
update user set password=#{password:VARCHAR}
                , name = #{name:VARCHAR}
                , address=#{address:VARCHAR}
                , phone=#{phone:VARCHAR}
                , allergy=#{allergy:VARCHAR}
where  id = #{id:VARCHAR}

mapper.select:
select count(*) as cnt from user

	
		
			
				
